import React from 'react';
import useMediaQuery from '../../hooks/useMediaQuery';
import AppBar from '../../components/AppBar';
import SocialMedia from '../../components/SocialMedia'; // Import your social media component

const variants = {
  hidden: (isTabletView) => ({ y: isTabletView ? -81 : -131 }),
  show: { y: 0 },
};

const Footer = () => {
  const isTabletView = useMediaQuery(
    ({ breakpoints }) => `(max-width:${breakpoints.sizes.tablet}px)`
  );

  return (
    <AppBar
      key={isTabletView}
      direction="up"
      showLogo={false} // Set to false to hide the logo in footer
      variants={variants}
      initial={false}
      custom={isTabletView}
      transition={{
        duration: 0.7,
        ease: [0.666, 0, 0.237, 1],
      }}
    >
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <h3>Your Footer Title</h3>
        <p>Your go-to source for innovative solutions.</p>
        <div className="contact-info">
          <p>Phone: +91.866.044.9970</p>
          <p>Email: info@luminexa.in</p>
        </div>
        <SocialMedia /> {/* Render your social media links */}
        <p>@luminexa 2024</p>
      </div>
    </AppBar>
  );
};

export default React.memo(Footer);
