import React, { useEffect, useRef } from 'react';
import Link from 'next/link';
import { motion } from 'framer-motion';
import LocomotiveScroll from 'locomotive-scroll';
import useCursorStyle from '../../../hooks/useCursorStyle';
import useMediaQuery from '../../../hooks/useMediaQuery';
import Arrow from '../../Icons/Arrow';
import Contact from './../../Home/Contact';
import {
  ContentSection,
  ProjectAnchor,
  ProjectInfo,
  ProjectTitle,
  ImagePreview,
  AboutDescription,
  MenuButton,
} from './styles';

const services = [
  { title: "Web Development", description: "Building responsive and functional websites.", image: "web-development.jpg" },
  { title: "AR Solutions", description: "Creating immersive augmented reality experiences.", image: "ar-solutions.jpg" },
  { title: "Design Services", description: "Providing unique design solutions tailored to your needs.", image: "design-services.jpg" },
];

const ServicesPage = () => {
  const scrollRef = useRef(null);
  const { addCursorBorder, removeCursorBorder } = useCursorStyle();
  const isTabletView = useMediaQuery(
    ({ breakpoints }) => `(max-width:${breakpoints.sizes.tablet}px)`,
  );

  useEffect(() => {
    const scroll = new LocomotiveScroll({
      el: scrollRef.current,
      smooth: true,
    });

    return () => {
      scroll.destroy();
    };
  }, []);

  const handleAnchorHoverStart = () => {
    addCursorBorder();
  };

  const handleAnchorHoverEnd = () => {
    removeCursorBorder();
  };

  return (
    <div data-scroll-container ref={scrollRef}>
      <ContentSection data-scroll-section>
        <h1>Our Services</h1>

        <div className="service-grid">
          {services.map((service, index) => (
            <ProjectAnchor
              key={index}
              onHoverStart={handleAnchorHoverStart}
              onHoverEnd={handleAnchorHoverEnd}
              data-scroll
              data-scroll-speed={index + 1}
            >
              <ProjectInfo>
                <h3>{service.title}</h3>
                <ProjectTitle>
                  {service.title.toUpperCase()}
                  <span className="arrow">
                    <Arrow />
                  </span>
                </ProjectTitle>
                <p>{service.description}</p>
              </ProjectInfo>
              <ImagePreview>
                <img src={`/${service.image}`} alt={service.title} />
              </ImagePreview>
            </ProjectAnchor>
          ))}
        </div>

        <AboutDescription>
          <h2>
            Explore our diverse range of services designed to help you succeed in today's competitive landscape.
          </h2>
        </AboutDescription>
        
        <Contact />
      </ContentSection>
    </div>
  );
};

export default ServicesPage;
