import React, { useEffect, useRef, useState } from 'react';
import Link from 'next/link';
import { motion } from 'framer-motion';
import LocomotiveScroll from 'locomotive-scroll';
import useCursorStyle from '../../../hooks/useCursorStyle';
import useMediaQuery from '../../../hooks/useMediaQuery';
import Arrow from '../../Icons/Arrow';
import Contact from './../../Home/Contact';
import {
  ContentSection,
  ProjectAnchor,
  ProjectInfo,
  ProjectTitle,
  ImagePreview,
  AboutDescription,
  ServiceCard,
} from './styles';

const services = [
  { title: "Web Development", description: "Building responsive and functional websites." },
  { title: "AR Solutions", description: "Creating immersive augmented reality experiences." },
  { title: "Design Services", description: "Providing unique design solutions tailored to your needs." },
];

const ServicesPage = () => {
  const scrollRef = useRef(null);
  const [scroll, setScroll] = useState(null);
  const { addCursorBorder, removeCursorBorder } = useCursorStyle();
  const isTabletView = useMediaQuery(
    ({ breakpoints }) => `(max-width:${breakpoints.sizes.tablet}px)`,
  );

  useEffect(() => {
    const locomotiveScroll = new LocomotiveScroll({
      el: scrollRef.current,
      smooth: true,
    });
    setScroll(locomotiveScroll);

    return () => {
      locomotiveScroll.destroy();
    };
  }, []);

  const handleAnchorHoverStart = (event) => {
    addCursorBorder();
  };

  const handleAnchorHoverEnd = (event) => {
    removeCursorBorder();
  };

  return (
    <div data-scroll-container ref={scrollRef}>
      <ContentSection data-scroll-section>
        <h1>Our Services</h1>

        <div className="service-grid">
          {services.map((service, index) => (
            <ServiceCard key={index} data-scroll data-scroll-speed={index + 1}>
              <Link href={`/services/${service.title.toLowerCase().replace(" ", "-")}`} passHref>
                <ProjectAnchor
                  onHoverStart={handleAnchorHoverStart}
                  onHoverEnd={handleAnchorHoverEnd}
                >
                  <ProjectInfo>
                    <h3>{service.title}</h3>
                    <ProjectTitle>
                      {service.title.toUpperCase()}
                      <span className="arrow">
                        <Arrow />
                      </span>
                    </ProjectTitle>
                    <p>{service.description}</p>
                  </ProjectInfo>
                  <ImagePreview>
                    <img src={`/${service.title.toLowerCase().replace(" ", "-")}.jpg`} alt={service.title} />
                  </ImagePreview>
                </ProjectAnchor>
              </Link>
            </ServiceCard>
          ))}
        </div>

        <AboutDescription>
          <h2>
            Explore our diverse range of services designed to help you succeed in today's competitive landscape.
          </h2>
        </AboutDescription>
        
        <Contact />
      </ContentSection>
    </div>
  );
};

export default ServicesPage;
