import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import SocialMedia from '../../SocialMedia'; // Ensure you have a SocialMedia component
import { secondaryFontStyle } from '../../../styles/shared/text';

const FooterContainer = styled(motion.footer)`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px 20px;
  background-color: ${({ theme }) => theme.colors.background};
  color: ${({ theme }) => theme.colors.text};

  & .logo {
    margin-bottom: 20px;
  }

  & .paragraph {
    text-align: center;
    margin-bottom: 40px;
    ${secondaryFontStyle};
  }

  & .contact-info {
    display: flex;
    justify-content: space-between;
    width: 100%;
    max-width: 800px; // Limit the width for better readability
    margin-bottom: 20px;

    & .column {
      flex: 1;
      text-align: center;
    }
  }

  & .social-media {
    margin-bottom: 20px;
  }

  & .footer-note {
    font-size: 14px;
    text-align: center;
  }
`;

const Footer = () => {
  return (
    <FooterContainer>
      <div className="logo">
        <img src="/path/to/logo.png" alt="Logo" /> {/* Replace with your logo path */}
      </div>
      <div className="paragraph">
        <p>Your go-to source for innovative solutions. We're here to help you achieve your goals.</p>
      </div>
      <div className="contact-info">
        <div className="column">
          <p>Phone: +91.866.044.9970</p>
        </div>
        <div className="column">
          <p>Address: 14 1st Stage Bhanashankari, Bangalore, KA IN</p>
        </div>
      </div>
      <div className="contact-info">
        <div className="column">
          <p>Email: info@luminexa.in</p>
        </div>
        <div className="column">
          <p>Address: 14 1st Stage Bhanashankari, Bangalore, KA IN</p>
        </div>
      </div>
      <div className="social-media">
        <SocialMedia />
      </div>
      <div className="footer-note">
        <p>@luminexa 2024</p>
      </div>
    </FooterContainer>
  );
};

export default React.memo(Footer);
