import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import containerStyles from '../../styles/shared/container';

const CTASection = styled(motion.section)`
  ${containerStyles};
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  margin-bottom: 100px;
`;

const CTAHeading = styled.h2`
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 20px;
`;

const CTADescription = styled.p`
  max-width: 600px;
  margin-bottom: 40px;
  line-height: 1.5;
  font-size: 1rem;
`;

const CTAButton = styled.a`
  padding: 15px 30px;
  font-size: 1rem;
  color: white;
  background-color: ${({ theme }) => theme.colors.red};
  border: none;
  text-decoration: none;
  transition: background-color 0.3s;

  &:hover {
    background-color: ${({ theme }) => theme.colors.darkRed};
  }
`;

const CallToAction = () => {
  return (
    <CTASection initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
      <CTAHeading>Unlock the potential of AR and AI in your business</CTAHeading>
      <CTADescription>
        Click to explore our transformative solutions in augmented reality, web development, and smart detection technologies. 
        Start your digital revolution now!
      </CTADescription>
      <CTAButton href="/explore">Explore Solutions</CTAButton>
    </CTASection>
  );
};

export default CallToAction;
