import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import containerStyles from '../../styles/shared/container';

const InfoContainer = styled(motion.div)`
  display: flex;
  justify-content: space-around; /* Space out the sections */
  flex-wrap: wrap; /* Allow wrapping for smaller screens */
  margin-bottom: 100px;
`;

const InfoSection = styled.div`
  ${containerStyles};
  flex: 1; /* Allow sections to grow */
  min-width: 300px; /* Minimum width for responsiveness */
  margin: 20px; /* Space between sections */
  background-color: ${({ theme }) => theme.colors.lightGray}; /* Adjust color as needed */
  padding: 50px 20px;
  text-align: center;
`;

const InfoHeading = styled.h2`
  width: 80%;
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 20px;
  color: black;
`;

const InfoDescription = styled.p`
  width: 82%;
  margin-bottom: 40px;
  line-height: 1.5;
  font-size: 1rem;
  color: black;
`;

const InformationSections = () => {
  return (
    <InfoContainer initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
      <InfoSection>
        <InfoHeading>What:</InfoHeading>
        <InfoDescription>
          Luminexa Technologies developed Image Tracking virtual software based on Augmented reality and Mixed Reality, they named it as nexAR.
        </InfoDescription>
      </InfoSection>

      <InfoSection>
        <InfoHeading>nexAR:</InfoHeading>
        <InfoDescription>
          nexAR is a WebAR solution that displays content like 3D models, holographic avatars, videos, images, audio, and links when the nexAR camera is pointed at a 2D surface.
        </InfoDescription>
      </InfoSection>

      <InfoSection>
        <InfoHeading>No:</InfoHeading>
        <InfoDescription>
          nexAR requires no additional app installations and works seamlessly on all smartphones, whether Android or iOS.
        </InfoDescription>
      </InfoSection>
    </InfoContainer>
  );
};

export default InformationSections;
