import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import containerStyles from '../../styles/shared/container';

const CTASection = styled(motion.section)`
  ${containerStyles};
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  margin-bottom: 100px;
  background-color: ${({ theme }) => theme.colors.red};
  padding: 100px 0;
`;

const SectionsContainer = styled.div`
  display: flex;
  justify-content: space-around; /* Space sections evenly */
  width: 100%;

  @media (max-width: 768px) {
    flex-direction: column; /* Stack sections vertically on mobile */
    align-items: center; /* Center align items in mobile view */
  }
`;

const Section = styled.div`
  flex: 1; /* Make each section take equal space */
  margin: 0 20px; /* Add some margin between sections */
  max-width: 300px; /* Set a max-width for better responsiveness */
  text-align: center; /* Center text in sections */

  @media (max-width: 768px) {
    margin: 20px 0; /* Add vertical margin for mobile */
    max-width: 100%; /* Allow full width on mobile */
  }
`;

const GIFIcon = styled.img`
  width: 100px; /* Adjust size as needed */
  height: auto;
  cursor: pointer; /* Change cursor to pointer for interactivity */
  transition: transform 0.3s;

  &:hover {
    transform: scale(1.1); /* Slightly scale on hover */
  }
`;

const CTAHeading = styled.h2`
  font-size: 2rem;
  font-weight: 700;
  margin: 20px 0; /* Adjust margin to create spacing */
  color: black;
`;

const CTADescription = styled.p`
  margin-bottom: 40px;
  line-height: 1.5;
  font-size: 1rem;
  color: black;
`;

const CallToAction = () => {
  return (
    <CTASection initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
      <SectionsContainer>
        <Section>
          <GIFIcon src="path/to/your/gif1.gif" alt="GIF Icon 1" onClick={() => window.location.href = "tel:8660449970"} />
          <CTAHeading>Section 1 Title</CTAHeading>
          <CTADescription>
            Description for section 1. Explain your first point here.
          </CTADescription>
        </Section>
        
        <Section>
          <GIFIcon src="path/to/your/gif2.gif" alt="GIF Icon 2" onClick={() => window.location.href = "tel:8660449970"} />
          <CTAHeading>Section 2 Title</CTAHeading>
          <CTADescription>
            Description for section 2. Highlight another key feature here.
          </CTADescription>
        </Section>
        
        <Section>
          <GIFIcon src="path/to/your/gif3.gif" alt="GIF Icon 3" onClick={() => window.location.href = "tel:8660449970"} />
          <CTAHeading>Section 3 Title</CTAHeading>
          <CTADescription>
            Description for section 3. Summarize your final point here.
          </CTADescription>
        </Section>
      </SectionsContainer>
    </CTASection>
  );
};

export default CallToAction;
