import styled from 'styled-components';
import { motion } from 'framer-motion';
import { secondaryFontStyle } from '../../../styles/shared/text';

export const FooterContainer = styled(motion.footer)`
  display: flex;
  flex-direction: column;
  align-item:
  padding: 40px 20px;
  background-color: ${({ theme }) => theme.colors.background};
  color: ${({ theme }) => theme.colors.red};

  & .logo {
    margin-bottom: 20px;
    align-self: flex-start; // Align logo to the left
  }

  & .description {
    text-align: left; // Align description to the left
    margin-bottom: 40px;
    ${secondaryFontStyle};
  }

  & .contact-info {
    display: flex;
    flex-wrap: wrap; // Allow wrapping on smaller screens
    justify-content: space-between;
    width: 100%;
    max-width: 800px; // Limit the width for better readability
    margin-bottom: 20px;

    & .column {
      flex: 1;
      text-align: left; // Align text in columns to the left
      ${secondaryFontStyle}; // Include secondary font style
      margin-bottom: 10px; // Add spacing between rows on smaller screens
    }
  }

  & .social-media {
    margin-bottom: 20px;
    align-self: flex-start; // Align social media to the left
  }

  & .footer-note {
    font-size: 14px;
    align-self: flex-start; // Align footer note to the left
  }
`;
