import React from 'react';
import Link from 'next/link';
import Logo from '../Icons/Logo';
import { Slider, Container, StyledLink } from './styles';

const getStyles = (direction = '') => {
  if (direction === 'down') return { top: 0 };
  if (direction === 'up') return { bottom: 0 };

  return {};
};

const variants = {
  hidden: { y: -131 },
  show: { y: 0 },
};

const AppBar = (props) => {
  const {
    direction = 'down',
    logoProps = {},
    style: styleProp = {},
    children, // Allow children to be passed in for footer content
    ...rootProps
  } = props;
  const [hidden, setHidden] = React.useState(false);

  React.useEffect(() => {
    const handleScroll = () => {
      let shouldHide = false;
      let intersection = 105; // Default offset

      let currentYPosition = direction === 'down' ? window.scrollY : 
        document.documentElement.scrollTop + window.innerHeight;

      if (direction === 'up') {
        intersection = document.documentElement.scrollHeight - 105;
      }

      shouldHide = currentYPosition > intersection;
      if (shouldHide !== hidden) {
        setHidden(shouldHide);
      }
    };

    handleScroll();

    window.addEventListener('scroll', handleScroll, false);

    return () => {
      window.removeEventListener('scroll', handleScroll, false);
    };
  }, [hidden, direction]);

  const styles = getStyles(direction);

  return (
    <Slider
      variants={variants}
      initial="hidden"
      animate={hidden ? 'hidden' : 'show'}
      transition={{
        duration: 1,
        ease: [0.666, 0, 0.237, 1],
      }}
      style={{
        ...styles,
        ...styleProp,
      }}
      {...rootProps}
    >
      <Container>
        <Link href="/" passHref>
          <StyledLink title="nexar">
            <Logo {...logoProps} />
          </StyledLink>
        </Link>
        {/* MenuButton is removed to avoid showing the menu */}
        {children} {/* Render footer content here */}
      </Container>
    </Slider>
  );
};

export default React.memo(AppBar);
