import React from "react";
import styled from "styled-components";

const Card = () => {
  return (
    <StyledWrapper>
      <div className="card">
        <div className="btn1" />
        <div className="btn2" />
        <div className="btn3" />
        <div className="btn4" />
        <div className="card-int">
          <div className="hello">
            . . .
            <span className="hidden">nexAR</span>
          </div>
        </div>
        <div className="top">
          <div className="camera">
            <div className="int" />
          </div>
          <div className="speaker" />
        </div>
      </div>
    </StyledWrapper>
  );
};

const StyledWrapper = styled.div`
  .card {
  width: 210px;
  height: 400px;
  background: black;
  border-radius: 35px;
  border: 2px solid rgb(40, 40, 40);
  padding: 7px;
  position: relative;
  box-shadow: 2px 5px 15px rgba(0, 0, 0, 0.486);
